mapper.update:
update orders 
set delivery_status=#{delivery_status}
where order_id=#{order_id}

mapper.select:
select rnum, user_id,order_name,total_price, order_id, order_date, delivery_status, total_amount from( select rownum as rnum, user_id, order_name, total_price, order_id, order_date, delivery_status, total_amount
from ( select o.user_id, o.order_name, o.total_price, o.order_id, o.order_date, o.delivery_status, o.total_amount from orders o
where order_name like '%' || #{keyword} || '%'
where delivery_status = #{status}
where order_name like '%' || #{keyword} || '%' and delivery_status = #{status}
order by o.order_date desc ) where rownum <=#{pager.endRowNo} ) where rnum >=#{pager.startRowNo}

      select count(*)
      from orders



	  where order_name like '%' || #{keyword} || '%'


	  where delivery_status = #{status}


	  where order_name like '%' || #{keyword} || '%' and delivery_status = #{status}
